import { useState, useEffect } from 'react';
import './play-css.css';
import { clear } from '@testing-library/user-event/dist/clear';


const SnakeGame = () => {
    const ROWS = 10;
    const COLS = 20;
    const CELL_SIZE = 30;
    const [snakePosition, setSnakePosition] = useState(Math.floor(Math.random() * COLS * ROWS));
    const [delx, setDelx] = useState(1)
    const [diamondPosition, setDiamondPosition] = useState(Math.floor(Math.random() * COLS * ROWS));
    const [level, setLevel] = useState(1)
    const [score, setScore] = useState(0);

    useEffect(() => {
        placeSnake();
        placeDiamond();

    }, [diamondPosition, snakePosition])


    useEffect(() => {


        const moveInterval = setInterval(() => {
            if (delx == 1 && snakePosition % 20 == 0) {
                setSnakePosition(snakePosition - 19)
            }
            else if (delx == -1 && snakePosition % 20 == 1) {
                setSnakePosition(snakePosition + 19)
            }
            else {
                setSnakePosition((snakePosition + delx) % 200)
            }
            removeSnake()


        }, 1000)
        if (toggle == 0) {
            clearInterval(moveInterval)
        }
        else if (toggle == 1) {
            setInterval(moveInterval)
        }
        return () => { clearInterval(moveInterval) }
    }, [snakePosition, toggleGame]);



    const placeDiamond = () => {
        const diamond = document.querySelector('.grid #_' + diamondPosition)
        diamond.style.backgroundColor = 'blue'
    }
    const removeDiamond = () => {
        const diamond = document.querySelector('.grid #_' + diamondPosition)
        diamond.style.backgroundColor = 'black'

    }
    const placeSnake = () => {
        const snake = document.querySelector('.grid #_' + snakePosition)
        snake.style.backgroundColor = 'red'
    }
    const removeSnake = () => {
        const snake = document.querySelector('.grid #_' + snakePosition)
        snake.style.backgroundColor = 'black'
    }

    const diamondClick = (e) => {
        const clicked = e.target
        if ((clicked.getAttribute('id')) == '_' + diamondPosition) {
            setScore(score + 10)
            setLevel(level + 1)
            removeDiamond()
            setDiamondPosition(Math.floor(Math.random() * COLS * ROWS))
        }

    }
    const toggleGame = (e) => {
        if (e.target.text == 'Start') {
            e.target.text = 'Stop'
            clearInterval(moveInterval)



        }

    }





    // const handleKeyDown = (e) => {
