    //             movePlayer(-CELL_SIZE, 0);
    //             break;
    //         case 38: // Up arrow
    //             movePlayer(0, -CELL_SIZE);
    //             break;
    //         case 39: // Right arrow
    //             movePlayer(CELL_SIZE, 0);
    //             break;
    //         case 40: // Down arrow
    //             movePlayer(0, CELL_SIZE);
    //             break;
    //         default:
    //             break;
    //     }
    // };

    // const movePlayer = (dx, dy) => {
    //     const newX = playerPosition.x + dx;
    //     const newY = playerPosition.y + dy;
    //     if (newX >= 0 && newX < COLS * CELL_SIZE && newY >= 0 && newY < ROWS * CELL_SIZE) {
    //         setPlayerPosition({ x: newX, y: newY });
    //         checkCollision(newX, newY);
    //     }
    // };

    // const checkCollision = (x, y) => {
    //     if (x === diamondPosition.x && y === diamondPosition.y) {
    //         setScore(score + 10);
    //         placeDiamond();
    //     }
    // };

    return (
        <>
            <div onClick={diamondClick}
                className="grid"
                style={{
                    gridTemplateColumns: `repeat(${COLS}, ${CELL_SIZE}px)`,
                    gridTemplateRows: `repeat(${ROWS}, ${CELL_SIZE}px)`,
                }}
            >
                {Array.from({ length: ROWS * COLS }, (_, index) => (
                    < div key={index} id={'_' + (index + 1)} />
                ))}
            </div>

            <div>Score: {score}</div>
            <div>Level: {level}</div>
            <button onClick={toggleGame}>Start</button>

        </>
    );
};

export default SnakeGame;
