import { useState, useEffect } from 'react';
import './play-css.css';
import { clear } from '@testing-library/user-event/dist/clear';


const SnakeGame = () => {
    const ROWS = 10;
    const COLS = 20;
    const CELL_SIZE = 30;
    const dir = [1, -20, 1, 20]
    const [level, setLevel] = useState(1)
    const getNextPosition = (snakeHead, delx) => {
        var newPos = 1;
        if (delx == 1 && snakeHead % 20 == 0) {
            newPos = (snakeHead - 19)
        }
        else if (delx == -1 && snakeHead % 20 == 1) {
            newPos = (snakeHead + 19)
        }
        else {
            newPos = snakeHead == snakeHead + delX == 200 ? 200 : (snakeHead + delx + 200) % 200
        }
        return newPos
    }
    const createSnake = (length) => {
        var head = Math.floor(Math.random() * COLS * ROWS)
        const dirIndex = Math.floor(Math.random() * 4)
        const direct = dir[dirIndex]
        const snakeDots = [head]

        for (let i = 1; i < length; i++) {
            const newHead = getNextPosition(head, -direct)
            snakeDots.unshift(newHead)
            head = newHead
        }
        return { dots: [100, 120], direction: 20 }

    }
    const [snakes, setSnakes] = useState(() => {
        const a = createSnake(level + 1)
        console.log("snake initially", a.dots)
        return [a]

    });
    console.log("Hi", snakes)
    const [diamondPosition, setDiamondPosition] = useState(Math.floor(Math.random() * COLS * ROWS));
    const [refresh, setRefresh] = useState(1)
    const [score, setScore] = useState(0);
    const [toggleGame, setToggleGame] = useState(1)

    useEffect(() => {
        placeDiamond();

        placeSnake();

    }, [diamondPosition, snakes])

    // useEffect(() => {
    //     const direction = setInterval(
    //         updateDirection
    //         , 4000)
    //     if (toggleGame == 0) clearInterval(direction)
    //     return () => { clearInterval(direction) }
    // }, [toggleGame])

    const refreshButton = () => {
        setRefresh(refresh + 1)
    }




    useEffect(() => {
        const moveInterval = setInterval(() => {
            updatePosition()

        }, 3000)

        if (toggleGame == 0) {
            clearInterval(moveInterval)
        }
        return () => { clearInterval(moveInterval); }
    }, [toggleGame, snakes]);



    const placeDiamond = () => {
        const diamond = document.querySelector('.grid #_' + diamondPosition)
        diamond.style.backgroundColor = 'blue'
    }
    const removeDiamond = () => {
        const diamond = document.querySelector('.grid #_' + diamondPosition)
        diamond.style.backgroundColor = 'black'

    }
    const placeSnake = () => {

        for (let i = 0; i < snakes.length; i++) {
            const dots = snakes[i].dots
            console.log("Placing snakes")
            for (let j = 0; j < dots.length; j++) {
                console.log(dots.length)
                const dot = document.querySelector('.grid #_' + dots[j])
                dot.style.backgroundColor = 'red'

            }

        }

    }


    const diamondClick = (e) => {
        const clicked = e.target
        if ((clicked.getAttribute('id')) == '_' + diamondPosition) {
            setScore(score + 10)
            setLevel(level + 1)
            removeDiamond()
            setDiamondPosition(Math.floor(Math.random() * COLS * ROWS))
        }

    }


    const updateDirection = () => {
        for (let i = 0; i < snakes.length; i++) {
            const dirArr = dir.filter((item) => item != -snakes[i].direction);
            const dirIndex = Math.floor(Math.random() * 3)
            snakes[i].direction = dirArr[dirIndex]
        }
    }
    const updatePosition = () => {
        console.log("Updating position")
        const temp = [...snakes]
        for (let i = 0; i < temp.length; i++) {
            const head = temp[i].dots[temp[i].dots.length - 1]
            // console.log("Tail Initially", temp[i].dots[0], snakes[i].dots[0])
            const direction = temp[i].direction
            const newHeadPos = getNextPosition(head, direction)
            temp[i].dots.push(newHeadPos)
            temp[i].dots.shift()
            console.log(temp[i].dots)
            const snakeTailPrev = document.querySelector('.grid #_' + snakes[i].dots[0])
            // console.log("Updating position", "New head", newHeadPos, temp[i].dots.length, snakes[i].dots.length, temp[i].dots[0], snakes[i].dots[1])
            snakeTailPrev.style.backgroundColor = 'black'


        }
        // setSnakes(temp)
    }


    // const checkCollision = (x, y) => {
    //     if (x === diamondPosition.x && y === diamondPosition.y) {
    //         setScore(score + 10);
    //         placeDiamond();
    //     }
    // };

    return (
        <>
            <div onClick={diamondClick}
                className="grid"
                style={{
                    gridTemplateColumns: `repeat(${COLS}, ${CELL_SIZE}px)`,
                    gridTemplateRows: `repeat(${ROWS}, ${CELL_SIZE}px)`,
                }}
            >
                {Array.from({ length: ROWS * COLS }, (_, index) => (
                    < div key={index} id={'_' + (index + 1)} />
                ))}
            </div>

            <div>Score: {score}</div>
            <div>Level: {level}</div>
            <button onClick={() => { setToggleGame(1 - toggleGame) }}>{toggleGame ? 'Stop' : 'Start'}</button>
            <button onClick={refreshButton}>Refresh</button>

        </>
    );
};

export default SnakeGame;
