import { useState, useEffect } from 'react';
import PlayArea from "./playArea";




const SnakeGame = () => {
    const ROWS = 10;
    const COLS = 20;
    const CELL_SIZE = 30;
    const [snakePosition, setSnakePosition] = useState(Math.floor(Math.random() * COLS * ROWS));
    const [diamondPosition, setDiamondPosition] = useState(Math.floor(Math.random() * COLS * ROWS));
    const [score, setScore] = useState(0);

    useEffect(() => {
        // placeSnake();
        // placeDiamond();
    }, []);



    const handleKeyDown = (e) => {
        switch (e.keyCode) {
            case 37: // Left arrow
                movePlayer(-CELL_SIZE, 0);
                break;
            case 38: // Up arrow
                movePlayer(0, -CELL_SIZE);
                break;
            case 39: // Right arrow
                movePlayer(CELL_SIZE, 0);
                break;
            case 40: // Down arrow
                movePlayer(0, CELL_SIZE);
                break;
            default:
                break;
        }
    };

    const movePlayer = (dx, dy) => {
        const newX = playerPosition.x + dx;
        const newY = playerPosition.y + dy;
        if (newX >= 0 && newX < COLS * CELL_SIZE && newY >= 0 && newY < ROWS * CELL_SIZE) {
            setPlayerPosition({ x: newX, y: newY });
            checkCollision(newX, newY);
        }
    };

    const checkCollision = (x, y) => {
        if (x === diamondPosition.x && y === diamondPosition.y) {
            setScore(score + 10);
            placeDiamond();
        }
    };

    return (
        <>
            <PlayArea />
            <div style={{ position: 'absolute', top: playerPosition.y, left: playerPosition.x, width: CELL_SIZE, height: CELL_SIZE, backgroundColor: 'green' }}></div>
            <div style={{ position: 'absolute', top: diamondPosition.y, left: diamondPosition.x, width: CELL_SIZE, height: CELL_SIZE, backgroundColor: 'blue' }}></div>
            <div>Score: {score}</div>
        </>
    );
};

export default SnakeGame;
